function increase(){var e=document.getElementById("body"),t=window.getComputedStyle(e,null).getPropertyValue("font-size"),n=parseFloat(t);e.style.fontSize=n+1+"px"}function decrease(){var e=document.getElementById("body"),t=window.getComputedStyle(e,null).getPropertyValue("font-size"),n=parseFloat(t);e.style.fontSize=n-1+"px"}function notify(){if("Notification"in window)if("granted"===Notification.permission)new Notification("Hallow :3!");else"denied"!==Notification.permission&&Notification.requestPermission(function(e){if("granted"===e)new Notification("Hey hey!")});else alert("This browser does not support desktop notification")}document.addEventListener("DOMContentLoaded",function(){document.getElementById("big").addEventListener("click",increase),document.getElementById("small").addEventListener("click",decrease),document.getElementById("vibrate").addEventListener("click",startVibrate),document.getElementById("not").addEventListener("click",notify)});var startVibrate=function(){window.navigator.vibrate(1e3)};